#!/usr/bin/env python3
class Menu:
    import PySimpleGUI as sg
    import new
    import expand

    def __init__(self):
        self.window = []
        self.layout = []
        self.event = []
        self.values = []
        self.create_window()

    def get_layout(self):
        datastore_image = ""
        expand_datastore_image = ""
        # old icon
        # info_datastore_image = ""
        info_datastore_image = ""
        # Build PySimpleGUI window
        self.sg.theme('DarkBlue13')
        self.layout = [
            [self.sg.Frame(layout=[[self.sg.Text('', pad=0), self.sg.Button('', key='new', mouseover_colors='#06193d', pad=11,
              image_data=datastore_image, auto_size_button=True), self.sg.Button('', mouseover_colors='#06193d',
              pad=10, key='expand', image_data=expand_datastore_image), self.sg.Button('',
              mouseover_colors='#06193d',  pad=(10,0), key='info', image_data=info_datastore_image),
              self.sg.Text('', pad=0),],
            [self.sg.Text('       New Datastore', click_submits=True, size=(20, 1), font='Helvetica'),
             self.sg.Text('Expand Datastore', size=(20, 1), font='Helvetica'), self.sg.Text('      Exit',
             size=(13, 1), font='Helvetica')]], title='', pad=1, relief=self.sg.RELIEF_RAISED)]
        ]

    def create_window(self):
        self.get_layout()
        self.window = self.sg.Window("Datastore Allocation                                                          v0.1.4.1", self.layout, use_custom_titlebar=True, titlebar_icon='', titlebar_background_color='#06193d', grab_anywhere=True) #no_titlebar=True
        while True:
            self.event, self.values = self.window.read()
            if self.event == self.sg.WIN_CLOSED or self.event == 'Exit':
                self.window.close()
                break   # Breaks the loop if exit is triggered
            if self.event in ('new', None):
                print('Create new Datastore')
                # window.close()
                self.new.new()
            elif self.event in ('expand', None):
                print('Expand Datastore')
                # window.close()
                self.expand.expand()
            elif self.event in ('info', None):
                print('Gather Datastore info')
                self.window.close()
                break
                # window.close()


def menu():
    Menu()

